import React, { useState, useEffect } from 'react';
import Button from '@mui/material/Button';
import { BrowserSerial } from "browser-serial";



const UserInterface = () => {
    const serial = new BrowserSerial({ serialOptions: { baudRate: 115200 } });
    let ConnectedPortWriter;
    let writer
    const [IsConnected, setIsConnected] = useState(false);
    const [ControlMode, setControlMode] = useState();
    const [Mode, setMode] = useState('D') // Driving
    // const handleKeyPress = (event) => {
    //     CommandHandler(event.code)
    // }

    useEffect(() => {
        document.addEventListener('keydown', handleKeyPress, true)
    }, [])

    useEffect(() => {
        console.log(IsConnected); // Log whenever IsConnected changes
    }, [IsConnected]);

    const handleKeyPress = (keypressed) => {
        console.log(keypressed)
        CommandHandler({ Mode: Mode, PressedKeyCode: keypressed.code })
    }

    const CommandHandler = ({ Mode, PressedKeyCode }) => {
        let MessageToSend = ''
        if (Mode === 'D') { // Driving Mode
            switch (PressedKeyCode) {
                case 'KeyA':
                    MessageToSend = 'L';
                    break;
                case 'KeyD':
                    MessageToSend = 'R';
                    break;
                case 'KeyS':
                    MessageToSend = 'B';
                    break;
                case 'KeyW':
                    MessageToSend = 'F';
                    break;
                default:
                    break;
            }
            if (MessageToSend) {
                // console.log()
                serial.write(`${Mode}${MessageToSend}`);
            }
        }
    }

    // const SerialSend = async ({ Prefix, Suffix }) => {
    //     await navigator.serial.getPorts().then(async (ports) => {
    //         console.log(ports);
    //         const encoder = new TextEncoder();
    //         writer = await ports[0].writable.getWriter()
    //         await writer.write(encoder.encode(`${Prefix}${Suffix}\n`));
    //         writer.releaseLock();
    //     })
    // }

    const OnUserClick = async () => {
        try { await serial.connect() }
        catch (err) { console.log(err) }
    }


    return (
        <div>
            <h1>Line Follower Console</h1>
            <Button onClick={OnUserClick} variant={IsConnected ? "contained" : "outlined"}>{IsConnected ? "Connected" : "Connect Device"}</Button>
            {/* <Button onClick={onUserClickDrivingMode} variant={ControlMode == 1 ? ("contained") : ("outlined")}>{ControlMode == 1 ? ("Driving Mode Enabled") : ("Enable Driving Mode")} </Button> */}
        </div>
    );
}

export default UserInterface
