import React, { useState, useEffect } from 'react';
import Button from '@mui/material/Button';
import ControlPanel from './Control-Panel/ControlPanel';
import { BrowserSerial } from "browser-serial";



const UserInterface = () => {
    const serial = new BrowserSerial({ serialOptions: { baudRate: 115200 } });
    let ConnectedPortWriter;
    let writer
    const [IsConnected, setIsConnected] = useState(false);
    const [ControlMode, setControlMode] = useState("");
    // const handleKeyPress = (event) => {
    //     CommandHandler(event.code)
    // }

    useEffect(() => {
        document.addEventListener('keydown', handleKeyPress, true)
    }, [])

    useEffect(() => {
        console.log(IsConnected); // Log whenever IsConnected changes
    }, [IsConnected]);

    const handleKeyPress = (keypressed) => {
        console.log(keypressed)
        CommandHandler({ Mode: ControlMode, PressedKeyCode: keypressed.code })
    }

    const CommandHandler = ({ Mode, PressedKeyCode }) => {
        let MessageToSend = ''
        if (Mode === 'D') { // Driving Mode
            switch (PressedKeyCode) {
                case 'KeyA':
                    MessageToSend = 'L';
                    break;
                case 'KeyD':
                    MessageToSend = 'R';
                    break;
                case 'KeyS':
                    MessageToSend = 'B';
                    break;
                case 'KeyW':
                    MessageToSend = 'F';
                    break;
                case 'Space':
                    MessageToSend = 'S';
                    break;
                default:
                    break;
            }
            if (MessageToSend) {
                console.log()
                serial.write(`${Mode}${MessageToSend}`);
            }
        }
    }

    const OnUserClick = async () => {
        if (IsConnected == false) {
            try {
                await serial.connect().then(() => {
                    setIsConnected(true)
                })
            }
            catch (err) { console.log(err) }
        } else {
            try {
                await serial.disconnect().then(() => {
                    setIsConnected(false)
                })
            } catch (e) {

            }
        }
    }
    const ChangeMode = ({ ModeSelected }) => {
        setControlMode(ModeSelected)
    }



    return (
        <div style={{ display: "flex" }}>
            <ControlPanel ModeSelected={ControlMode} ChangeMode={ChangeMode} />
            <div style={{ paddingLeft: "2vw" }}>
                <h2>Line Follower Console</h2>
                <Button onClick={OnUserClick} variant={IsConnected ? "contained" : "outlined"}>{IsConnected ? "Click to Disconnect" : "Click to Connect Device"}</Button>

            </div>
        </div >
    );
}

export default UserInterface
